namespace SpriteKind {
    export const Bar = SpriteKind.create()
    export const Beam = SpriteKind.create()
    export const SpawnPOint = SpriteKind.create()
    export const HeartPowerUp = SpriteKind.create()
    export const BeamChargePU = SpriteKind.create()
    export const BeamPowerPU = SpriteKind.create()
    export const Ascend = SpriteKind.create()
}
controller.B.onEvent(ControllerButtonEvent.Pressed, function () {
    if (GameOnBool == 1) {
        if (directionChangeCharges > 0) {
            music.play(music.melodyPlayable(music.knock), music.PlaybackMode.InBackground)
            PlayerBall.vy = velocityPlayerStored * -1
            directionChangeCharges += -1
        }
    }
})
sprites.onOverlap(SpriteKind.Beam, SpriteKind.Projectile, function (sprite, otherSprite) {
    music.play(music.melodyPlayable(music.zapped), music.PlaybackMode.InBackground)
    sprites.destroy(otherSprite, effects.disintegrate, 5)
    info.changeScoreBy(1)
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.HeartPowerUp, function (sprite, otherSprite) {
    music.play(music.melodyPlayable(music.magicWand), music.PlaybackMode.InBackground)
    sprites.destroy(otherSprite, effects.hearts, 5)
    Life += 1
})
controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    if (GameOnBool == 1) {
        music.play(music.melodyPlayable(music.pewPew), music.PlaybackMode.InBackground)
        Bool_Beam = 1
        for (let index = 0; index < Beam_Charges; index++) {
            Beam = sprites.createProjectileFromSprite(assets.image`Beam`, PlayerBall, 100, 0)
            Beam.setKind(SpriteKind.Beam)
            pause(50)
        }
        Beam_Charges = 0
        Bool_Beam = 0
    }
})
