// Power up code, deals with selection and sending the power ups out
forever(function () {
    if (GameOnBool == 1) {
        pause(randint(5000, 15000))
        RandomPowerUpSelection = randint(1, 3)
        if (1 == RandomPowerUpSelection) {
            HeartPU = sprites.createProjectileFromSprite(img`
                . f f . f f . 
                f 3 3 f 3 3 f 
                f 3 3 3 3 3 f 
                f 3 3 3 3 3 f 
                . f 3 3 3 f . 
                . . f 3 f . . 
                . . . f . . . 
                `, SpawnPoint, -50, randint(-20, 20))
            HeartPU.setKind(SpriteKind.HeartPowerUp)
        }
        if (2 == RandomPowerUpSelection) {
            BeamChargeSpeedPU = sprites.createProjectileFromSprite(img`
                . . . . . . . 
                . . . f f . . 
                f f f f 5 f . 
                f 5 5 5 5 5 f 
                f f f f 5 f . 
                . . . f f . . 
                . . . . . . . 
                `, SpawnPoint, -50, randint(-20, 20))
            BeamChargeSpeedPU.setKind(SpriteKind.BeamChargePU)
        }
        if (3 == RandomPowerUpSelection) {
            BeamPowerPU = sprites.createProjectileFromSprite(img`
                . . f f f . . 
                . f 5 1 5 f . 
                f 5 1 5 5 5 f 
                f 1 5 5 5 1 f 
                f 5 5 5 1 5 f 
                . f 5 1 5 f . 
                . . f f f . . 
                `, SpawnPoint, -50, randint(-20, 20))
            BeamPowerPU.setKind(SpriteKind.BeamPowerPU)
        }
    }
})
